DROP TABLE IF EXISTS `java_deploy`;
CREATE TABLE `java_deploy` (
  `uuid` varchar(64) NOT NULL COMMENT 'UUID',
  `name` varchar(255) DEFAULT NULL COMMENT '项目名称',
  `profile` varchar(255) DEFAULT NULL COMMENT '部署主机',
  `clunodes` varchar(255) DEFAULT NULL COMMENT '集群节点（空格分隔）',
  `installuser` varchar(255) DEFAULT NULL COMMENT '部署用户',
  `password` varchar(255) DEFAULT NULL COMMENT '密码',
  `port` varchar(255) DEFAULT NULL COMMENT 'ssh端口',
  `packagename` varchar(255) DEFAULT NULL COMMENT '版本包名',
  `installjdk` varchar(255) DEFAULT NULL COMMENT '是否安装JDK',
  `installzk` varchar(255) DEFAULT NULL COMMENT '是否安装zk',
  `installhadoop` varchar(255) DEFAULT NULL COMMENT '是否安装hadoop',
  `installkafka` varchar(255) DEFAULT NULL COMMENT '是否安装kafka',
  `installyita` varchar(255) DEFAULT NULL COMMENT '是否安装yita',
  `installportal` varchar(255) DEFAULT NULL COMMENT '是否安装portal',
  `jdknodes` varchar(255) DEFAULT NULL,
  `zknodes` varchar(255) DEFAULT NULL,
  `zkmaster` varchar(255) DEFAULT NULL,
  `zkport` varchar(255) DEFAULT NULL,
  `namenode` varchar(255) DEFAULT NULL,
  `datanode` varchar(255) DEFAULT NULL,
  `hdfsnodes` varchar(255) DEFAULT NULL,
  `dfsnamedirs` varchar(255) DEFAULT NULL,
  `dfsdatadirs` varchar(255) DEFAULT NULL,
  `kafkanodes` varchar(255) DEFAULT NULL,
  `kafkaport` varchar(255) DEFAULT NULL,
  `logdirs` varchar(255) DEFAULT NULL,
  `yitanodes` varchar(255) DEFAULT NULL,
  `yitamaster` varchar(255) DEFAULT NULL,
  `slavenodes` varchar(255) DEFAULT NULL,
  `memxmx` varchar(255) DEFAULT NULL,
  `memxms` varchar(255) DEFAULT NULL,
  `model` varchar(255) DEFAULT NULL,
  `mysqlnode` varchar(255) DEFAULT NULL,
  `mysqlport` varchar(255) DEFAULT NULL,
  `mysqluser` varchar(255) DEFAULT NULL,
  `mysqlpasswd` varchar(255) DEFAULT NULL,
  `portalnodes` varchar(255) DEFAULT NULL,
  `portalserver` varchar(255) DEFAULT NULL,
  `portaljar` varchar(255) DEFAULT NULL,
  `infojar` varchar(255) DEFAULT NULL,
  `portalport` varchar(255) DEFAULT NULL,
  `portaldb` varchar(255) DEFAULT NULL,
  `portalsql` varchar(255) DEFAULT NULL,
  `installpath` varchar(255) DEFAULT NULL,
  `type` varchar(255) DEFAULT NULL COMMENT '部署类型：1-免密 2-部署',
  PRIMARY KEY (`uuid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
